#include <bits/stdc++.h>using namespace std;using ll = long long; int main() {    ios::sync_with_stdio(false);    cin.tie(nullptr);     int t;     if (!(cin >> t)) return 0;    while (t--) {        int k;         ll x;         cin >> k >> x;        unordered_set<ll> cur;                cur.reserve(1<<min(20,k));        cur.insert(x);         for (int step = 0; step < k; ++step) {            unordered_set<ll> nxt;            nxt.reserve(cur.size()*2 + 16);            for (ll v : cur) {                            nxt.insert(v * 2);                                if ((v - 1) % 3 == 0) {                    ll y = (v - 1) / 3;                    if (y > 0 && (y % 2 == 1)) nxt.insert(y);                }            }            cur.swap(nxt);        }                 ll chosen = LLONG_MAX;        ll largestOdd = LLONG_MIN;         for (ll v : cur) {             if (v % 2 == 1)             largestOdd = max(largestOdd, v);            chosen = min(chosen, v);        }         if (largestOdd != LLONG_MIN) cout << largestOdd << '\n';               else cout << chosen << '\n';    }    return 0;}