#include<bits/stdc++.h>using namespace std;  int main(){ int t; cin>>t; while(t--){  int n;    long long m;    cin>>n>>m;    vector<int>v(n);    vector<int>v2(n);    for(int i=0;i<n;i++){        cin>>v[i]>>v2[i];    }     long long ans=0;    long long p1=0;    long long p2=0;    bool f=true;     for(int i=0;i<n;i++){        long long d=v[i]-p1;        int pt=p2^v2[i];         if(d<pt){            f=false;            break;         }        if((d%2)==pt) ans+=d;        else{            ans+=(d-1);                    }        p1=v[i];            p2=v2[i];    }    if(!f){        cout<<-1<<endl;        continue;    }     ans+=(m-p1);    cout<<ans<<endl;  }       return 0;}