#include<bits/stdc++.h>using namespace std;   int main(){    int t;    cin>>t;     while(t--){         int n;        cin>>n;        long long even=0;        vector<long long>vec;        for(int i=0;i<n;i++){            long long a;            cin>>a;            if(a%2==0) even+=a;            else vec.push_back(a);        }        if(vec.empty()){            cout<<0<<endl;          continue;         }        sort(vec.begin(),vec.end(),greater<long long>());        int t=(int((int)vec.size())+1)/2;         long long add=0;        for(int i=0;i<t;i++){            add+=vec[i];        }         cout<<even+add<<endl;           }     return 0;}